document.addEventListener("DOMContentLoaded",function(){const n=document.querySelector(".logo h1"),i=n.textContent;n.textContent="";let o=0;const r=()=>{o<i.length&&(n.textContent+=i.charAt(o),o++,setTimeout(r,100))};setTimeout(r,500);const s={root:null,rootMargin:"0px",threshold:.3},c=new IntersectionObserver(t=>{t.forEach(e=>{e.isIntersecting&&e.target.classList.add("animated")})},s);document.querySelectorAll(".menu-link, .social-link").forEach(t=>{c.observe(t)}),document.querySelectorAll("a").forEach(t=>{t.addEventListener("click",function(e){this.getAttribute("target")!=="_blank"&&(e.preventDefault(),this.classList.add("clicked"),setTimeout(()=>{window.location.href=this.href},300))})})});
